import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

import {
  TopBar,
  ProfileCard,
  FriendsCard,
  CustomButton,
  EditProfile,
  PostCard,
  TextInput,
  Loading,
} from "../components";

import { NoProfile } from "../assets";
import { BsFiletypeGif, BsPersonFillAdd } from "react-icons/bs";
import { BiImages, BiSolidVideo } from "react-icons/bi";
import { useForm } from "react-hook-form";

import {
  getFriendRequests,
  acceptFriendRequest,
  denyFriendRequest,
  getSuggestedFriends,
  sendFriendRequest,
} from "../api/FriendAPI";

// Dữ liệu mock cho posts
// import { posts } from "../assets/data";

const Home = () => {
  console.log("Home component rendered");
  const { user, edit } = useSelector((state) => state.user);

  const [friendRequests, setFriendRequests] = useState([]);
  const [suggestedFriends, setSuggestedFriends] = useState([]);
  const [errMsg, setErrMsg] = useState("");
  const [file, setFile] = useState(null);
  const [posting, setPosting] = useState(false);
  const [loading, setLoading] = useState(false);

  // React Hook Form
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  // Gọi API lấy friendRequests và suggestedFriends khi component mount
  useEffect(() => {
    if (user?._id) {
      fetchFriendRequests(user._id);
      fetchSuggestedFriends(user._id);
    }
  }, [user?._id]);

  // Hàm gọi API để lấy danh sách friend requests
  const fetchFriendRequests = async (userId) => {
    try {
      const data = await getFriendRequests(userId);
      console.log("Friend requests data:", data);
      setFriendRequests(data);
    } catch (error) {
      console.error("Lỗi khi lấy friend requests:", error);
    }
  };

  // Hàm gọi API để lấy danh sách gợi ý bạn bè
  const fetchSuggestedFriends = async (userId) => {
    try {
      const data = await getSuggestedFriends(userId);
      setSuggestedFriends(data);
    } catch (error) {
      console.error("Lỗi khi lấy gợi ý bạn bè:", error);
    }
  };

  // Chấp nhận lời mời kết bạn
  const handleAccept = async (requestId) => {
    try {
      await acceptFriendRequest(requestId);
      // Xoá request khỏi danh sách
      setFriendRequests((prev) =>
        prev.filter((req) => req._id !== requestId)
      );
      alert("Đã chấp nhận lời mời kết bạn!");
    } catch (error) {
      console.error("Lỗi khi chấp nhận lời mời:", error);
    }
  };

  // Từ chối lời mời kết bạn
  const handleDeny = async (requestId) => {
    try {
      await denyFriendRequest(requestId);
      setFriendRequests((prev) =>
        prev.filter((req) => req._id !== requestId)
      );
      alert("Đã từ chối lời mời kết bạn!");
    } catch (error) {
      console.error("Lỗi khi từ chối lời mời:", error);
    }
  };

  // Gửi lời mời kết bạn (từ gợi ý)
  const handleAddFriend = async (receiverId) => {
    try {
      await sendFriendRequest(user?._id, receiverId);
      alert("Đã gửi lời mời kết bạn!");
      // Loại bỏ user này khỏi danh sách gợi ý
      setSuggestedFriends((prev) =>
        prev.filter((f) => f._id !== receiverId)
      );
    } catch (error) {
      console.error("Lỗi khi gửi lời mời kết bạn:", error);
    }
  };

  // // Hàm handle post (chưa có API nên để trống)
  // const handlePostSubmit = async (data) => {
  //   console.log("Nội dung post:", data);
  //   console.log("File:", file);
  //   // Gọi API post nếu có
  // };

  //Post
  
  // Fetch posts on component mount
  useEffect(() => {
    const fetchPosts = async () => {
      setLoading(true);
      try {
        const { data } = await postAPI.fetchPosts();
        setPosts(data);
      } catch (error) {
        console.error("Failed to fetch posts:", error);
      } finally {
        setLoading(false);
      }
    };
    
    fetchPosts();
  }, []);

  // Handle file selection
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) return;

    // Validate file size (5MB max)
    if (selectedFile.size > 5 * 1024 * 1024) {
      setErrMsg({
        status: "failed",
        message: "File size should be less than 5MB",
      });
      return;
    }

    setFile(selectedFile);
    setErrMsg({});

    // Create preview
    const reader = new FileReader();
    reader.onload = () => setFilePreview(reader.result);
    reader.readAsDataURL(selectedFile);
  };

  // Handle post submission
  const handlePostSubmit = async (data) => {
    setPosting(true);
    try {
      const formData = new FormData();
      formData.append("description", data.description);
      if (file) formData.append("media", file);

      const { data: newPost } = await postAPI.createPost(formData);
      
      // Update posts list
      setPosts([newPost, ...posts]);
      
      // Reset form
      reset();
      setFile(null);
      setFilePreview(null);
      setErrMsg({
        status: "success",
        message: "Post created successfully!",
      });
    } catch (error) {
      console.error("Post creation error:", error);
      setErrMsg({
        status: "failed",
        message: error.response?.data?.message || "Failed to create post",
      });
    } finally {
      setPosting(false);
    }
  };

  // Handle post deletion
  const handleDeletePost = async (postId) => {
    if (!window.confirm("Are you sure you want to delete this post?")) return;
    
    try {
      await postAPI.deletePost(postId);
      setPosts(posts.filter(post => post._id !== postId));
    } catch (error) {
      console.error("Delete post error:", error);
    }
  };

  // Handle like post
  const handleLikePost = async (postId) => {
    try {
      await postAPI.likePost(postId);
      setPosts(posts.map(post => 
        post._id === postId
          ? {
              ...post,
              likes: post.likes.includes(user._id)
                ? post.likes.filter(id => id !== user._id)
                : [...post.likes, user._id],
            }
          : post
      ));
    } catch (error) {
      console.error("Like post error:", error);
    }
  };

  return (
    <>
      <div className='w-full px-0 lg:px-10 pb-20 2xl:px-40 bg-bgColor lg:rounded-lg h-screen overflow-hidden'>
        <TopBar />

        <div className='w-full flex gap-2 lg:gap-4 pt-5 pb-10 h-full'>
          {/* LEFT */}
          <div className='hidden w-1/3 lg:w-1/4 h-full md:flex flex-col gap-6 overflow-y-auto'>
            <ProfileCard user={user} />
            <FriendsCard friends={user?.friends} />
          </div>

          {/* CENTER CONTENT */}
          <div className='flex-1 h-full px-4 flex flex-col gap-6 overflow-y-auto rounded-lg'>
            {/* CREATE POST FORM */}
            <form
              onSubmit={handleSubmit(handlePostSubmit)}
              className='bg-primary px-4 rounded-lg'
            >
              <div className='w-full flex items-center gap-2 py-4 border-b border-[#66666645]'>
                <img
                  src={user?.profileUrl ?? NoProfile}
                  alt='User Image'
                  className='w-14 h-14 rounded-full object-cover'
                />
                <TextInput
                  styles='w-full rounded-full py-5'
                  placeholder="What's on your mind...."
                  name='description'
                  register={register("description", {
                    required: "Write something about post",
                  })}
                  error={errors.description ? errors.description.message : ""}
                />
              </div>

              {/* MEDIA PREVIEW */}
              {filePreview && (
                <div className="relative mt-2">
                  {file.type.startsWith("image/") ? (
                    <img
                      src={filePreview}
                      alt="Preview"
                      className="w-full h-64 object-contain rounded-lg"
                    />
                  ) : (
                    <video
                      src={filePreview}
                      controls
                      className="w-full h-64 object-contain rounded-lg"
                    />
                  )}
                  <button
                    type="button"
                    onClick={() => {
                      setFile(null);
                      setFilePreview(null);
                    }}
                    className="absolute top-2 right-2 bg-black bg-opacity-50 text-white p-1 rounded-full"
                  >
                    ×
                  </button>
                </div>
              )}

              {/* ERROR/SUCCESS MESSAGE */}
              {errMsg?.message && (
                <span
                  role='alert'
                  className={`text-sm ${
                    errMsg?.status === "failed"
                      ? "text-[#f64949fe]"
                      : "text-[#2ba150fe]"
                  } mt-0.5`}
                >
                  {errMsg?.message}
                </span>
              )}

              {/* POST ACTIONS */}
              <div className='flex items-center justify-between py-4'>
                <label
                  htmlFor='imgUpload'
                  className='flex items-center gap-1 text-base text-ascent-2 hover:text-ascent-1 cursor-pointer'
                >
                  <input
                    type='file'
                    onChange={handleFileChange}
                    className='hidden'
                    id='imgUpload'
                    accept='.jpg, .png, .jpeg, .mp4, .webm, .gif'
                  />
                  <BiImages />
                  <span>Media</span>
                </label>

                <div>
                  {posting ? (
                    <Loading />
                  ) : (
                    <CustomButton
                      type='submit'
                      title='Post'
                      containerStyles='bg-[#0444a4] text-white py-1 px-6 rounded-full font-semibold text-sm'
                      disabled={posting || (!file && !watch("description"))}
                    />
                  )}
                </div>
              </div>
            </form>

            {/* POSTS LIST */}
            {loading ? (
              <Loading />
            ) : posts?.length > 0 ? (
              posts?.map((post) => (
                <PostCard
                  key={post?._id}
                  post={post}
                  user={user}
                  deletePost={handleDeletePost}
                  likePost={handleLikePost}
                />
              ))
            ) : (
              <div className='flex w-full h-full items-center justify-center'>
                <p className='text-lg text-ascent-2'>No Posts Available</p>
              </div>
            )}
          </div>

          {/* RIGHT */}
          <div className='hidden w-1/4 h-full lg:flex flex-col gap-8 overflow-y-auto'>
            {/* FRIEND REQUEST */}
            <div className='w-full bg-primary shadow-sm rounded-lg px-6 py-5'>
              <div className='flex items-center justify-between text-xl text-ascent-1 pb-2 border-b border-[#66666645]'>
                <span> Friend Request</span>
                <span>{friendRequests?.length}</span>
              </div>

              <div className='w-full flex flex-col gap-4 pt-4'>
                {friendRequests?.map((request, index) => (
                  <div key={request._id || index} className='flex items-center justify-between'>
                    <Link
                      to={`/profile/${request.requestFrom?._id}`}
                      className='w-full flex gap-4 items-center cursor-pointer'
                    >
                      <img
                        src={request.requestFrom?.profileUrl ?? NoProfile}
                        alt={request.requestFrom?.firstName}
                        className='w-10 h-10 object-cover rounded-full'
                      />
                      <div className='flex-1'>
                        <p className='text-base font-medium text-ascent-1'>
                          {request.requestFrom?.firstName} {request.requestFrom?.lastName}
                        </p>
                        <span className='text-sm text-ascent-2'>
                          {request.requestFrom?.profession ?? "No Profession"}
                        </span>
                      </div>
                    </Link>

                    <div className='flex gap-1'>
                      <CustomButton
                        title='Accept'
                        containerStyles='bg-[#0444a4] text-xs text-white px-1.5 py-1 rounded-full'
                        onClick={() => handleAccept(request._id)}
                      />
                      <CustomButton
                        title='Deny'
                        containerStyles='border border-[#666] text-xs text-ascent-1 px-1.5 py-1 rounded-full'
                        onClick={() => handleDeny(request._id)}
                      />
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* SUGGESTED FRIENDS */}
            <div className='w-full bg-primary shadow-sm rounded-lg px-5 py-5'>
              <div className='flex items-center justify-between text-lg text-ascent-1 border-b border-[#66666645]'>
                <span>Friend Suggestion</span>
              </div>
              <div className='w-full flex flex-col gap-4 pt-4'>
                {suggestedFriends?.map((friend, index) => (
                  <div className='flex items-center justify-between' key={friend._id || index}>
                    <Link
                      to={`/profile/${friend?._id}`}
                      className='w-full flex gap-4 items-center cursor-pointer'
                    >
                      <img
                        src={friend?.profileUrl ?? NoProfile}
                        alt={friend?.firstName}
                        className='w-10 h-10 object-cover rounded-full'
                      />
                      <div className='flex-1 '>
                        <p className='text-base font-medium text-ascent-1'>
                          {friend?.firstName} {friend?.lastName}
                        </p>
                        <span className='text-sm text-ascent-2'>
                          {friend?.profession ?? "No Profession"}
                        </span>
                      </div>
                    </Link>

                    <div className='flex gap-1'>
                      <button
                        className='bg-[#0444a430] text-sm text-white p-1 rounded'
                        onClick={() => handleAddFriend(friend._id)}
                      >
                        <BsPersonFillAdd size={20} className='text-[#0f52b6]' />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Edit profile modal */}
      {edit && <EditProfile />}
    </>
  );
};

export default Home;
