import axios from 'axios';

// Tạo bài viết mới
export const createPost = async (userId, content, mediaFile) => {
  const formData = new FormData();
  formData.append("userId", userId);
  formData.append("content", new Blob([content], { type: "text/plain" }));

  if(mediaFile){
    mediaFile.forEach((file)=>{
        formData.append("mediaFile", file);
    });
  }
  console.log("FormData Entries:");
  for (let pair of formData.entries()) {
      console.log(pair[0], pair[1]);
  }

  const response = await axios.post(`/api/posts/create`, formData, {
    headers: {"Content-Type":"multipart/form-data"}
  });
  return response.data; 
};

// Chỉnh sửa bài viết
export const editPost = async(postId, content, mediaFiles) => {
    const formData = new FormData();
    formData.append("content", content);

    if(mediaFiles){
        mediaFiles.forEach((file)=>{
            formData.append("mediaFiles", file);
        });
      }

      const response = await axios.post(`/api/posts/update/${postId}`, formData, {
        headers: {"Content-Type":"multipart/form-data"}
      });
      return response.data; 

};

// Xóa bài viết
export const removePost = async(postId) => {
    const response = await axios.delete(`/api/posts/delete/${postId}`);
    return response.data;
};

// Like bài viết
export const likePost = async(postId, userId) => {
    const response = await axios.post(`/api/posts/like`, {postId, userId});
    return response.data;
};

// Bình luận bài viết
export const commentPost = async(postId, userId, comment) => {
    const response = await axios.post(`/api/posts/comment`, {postId, userId, comment});
    return response.data;
};

// Chia sẻ bài viết
export const sharePost = async(postId, userId) => {
    const response = await axios.post(`/api/posts/share`, {postId, userId});
    return response.data;
};

// Lấy danh sách bài viết
export const getPosts = async (userId) => {
  try {
      const response = await axios.get(`/api/posts?userId=${userId}`);
      console.log("API Response:", response.data); // Log dữ liệu từ API
      return response.data;
  } catch (error) {
      console.error("API Error:", error);
      return [];
  }
};
