import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import moment from "moment";
import { NoProfile } from "../assets";
import { BiComment, BiLike, BiShare, BiSolidLike } from "react-icons/bi";
import { MdOutlineDeleteOutline } from "react-icons/md";
import Loading from "./Loading";
import { useForm } from "react-hook-form";
import TextInput from "./TextInput";
import CustomButton from "./CustomButton";
import { getCommentsAPI } from "../api/PostAPI";
import { useDispatch } from "react-redux";

const ReplyCard = ({ reply, user, handleLike }) => {
  return (
    <div className='w-full py-3'>
      <div className='flex gap-3 items-center mb-1'>
        <Link to={"/profile/" + reply?.userId?._id}>
          <img
            src={reply?.userId?.profileUrl ?? NoProfile}
            alt={reply?.userId?.firstName}
            className='w-10 h-10 rounded-full object-cover'
          />
        </Link>

        <div>
          <Link to={"/profile/" + reply?.userId?._id}>
            <p className='font-medium text-base text-ascent-1'>
              {reply?.userId?.firstName} {reply?.userId?.lastName}
            </p>
          </Link>
          <span className='text-ascent-2 text-sm'>
            {moment(reply?.createdAt).fromNow()}
          </span>
        </div>
      </div>

      <div className='ml-12'>
        <p className='text-ascent-2 '>{reply?.comment}</p>
        <div className='mt-2 flex gap-6'>
          <p
            className='flex gap-2 items-center text-base text-ascent-2 cursor-pointer'
            onClick={handleLike}
          >
            {reply?.likes?.includes(user?._id) ? (
              <BiSolidLike size={20} color='blue' />
            ) : (
              <BiLike size={20} />
            )}
            {reply?.likes?.length} Likes
          </p>
        </div>
      </div>
    </div>
  );
};

const CommentForm = ({ user, id, replyAt, getComments }) => {
  const [loading, setLoading] = useState(false);
  const [errMsg, setErrMsg] = useState("");
  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    mode: "onChange",
  });

  const onSubmit = async (data) => {};

  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      className='w-full border-b border-[#66666645]'
    >
      <div className='w-full flex items-center gap-2 py-4'>
        <img
          src={user?.profileUrl ?? NoProfile}
          alt='User Image'
          className='w-10 h-10 rounded-full object-cover'
        />

        <TextInput
          name='comment'
          styles='w-full rounded-full py-3'
          placeholder={replyAt ? `Reply @${replyAt}` : "Comment this post"}
          register={register("comment", {
            required: "Comment can not be empty",
          })}
          error={errors.comment ? errors.comment.message : ""}
        />
      </div>
      {errMsg?.message && (
        <span
          role='alert'
          className={`text-sm ${
            errMsg?.status === "failed"
              ? "text-[#f64949fe]"
              : "text-[#2ba150fe]"
          } mt-0.5`}
        >
          {errMsg?.message}
        </span>
      )}

      <div className='flex items-end justify-end pb-2'>
        {loading ? (
          <Loading />
        ) : (
          <CustomButton
            title='Submit'
            type='submit'
            containerStyles='bg-[#0444a4] text-white py-1 px-3 rounded-full font-semibold text-sm'
          />
        )}
      </div>
    </form>
  );
};


const PostCard = ({ post, user, deletePost, likePost }) => {
  const [showAll, setShowAll] = useState(0);
  const [showReply, setShowReply] = useState(0);
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [replyComments, setReplyComments] = useState(0);
  const [showComments, setShowComments] = useState(0);
  const [error, setError] = useState(null);
  const postId = post.postId;
  
  // Hàm lấy comment
  const getComments = async () => {
    try {
      setLoading(true);
      const response = await getCommentsAPI(postId); // Đảm bảo hàm này đã được định nghĩa
      setComments(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching comments:', error);
      setLoading(false);
    }
  };

  // Hàm xử lý khi nhấn nút comment
  const handleShowComments = () => {
    // Nếu đang hiển thị comment của bài viết này thì ẩn đi
    if (showComments === postId) {
      setShowComments(null);
    } else {
      // Nếu không thì hiển thị comment
      setShowComments(postId);
      getComments(); // Gọi API lấy comment
    }
  };
  // Kiểm tra dữ liệu trước khi render
  if (!post) return null;

  

  return (
    <div className='mb-2 bg-primary p-4 rounded-xl'>
      {/* Phần header */}
      <div className='flex gap-3 items-center mb-2'>
        <Link to={`/profile/${post.userId}`}>
          <img
            src={user?.profileImage || NoProfile}
            alt={user?.username}
            className='w-14 h-14 object-cover rounded-full'
          />
        </Link>

        <div className='w-full flex justify-between'>
          <div>
            <Link to={`/profile/${post.userId}`}>
              <p className='font-medium text-lg text-ascent-1'>
                {user?.username}
              </p>
            </Link>
            <span className='text-ascent-2'>{user?.country}</span>
          </div>
          <span className='text-ascent-2'>
            {moment(post.timestamp).fromNow()}
          </span>
        </div>
      </div>

      {/* Nội dung bài viết */}
      <div>
        <p className='text-ascent-2'>
          {showAll ? post.content : post.content?.slice(0, 100)}
          {post.content?.length > 100 && (
            <span
              className='text-blue ml-2 font-medium cursor-pointer'
              onClick={() => setShowAll(!showAll)}
            >
              {showAll ? "Show Less" : "...Show More"}
            </span>
          )}
        </p>
        {post?.mediaURL && (
        post.mediaURL.endsWith('.mp4') || post.mediaURL.endsWith('.wav') ? (
          <video controls className='w-full mt-2 rounded-lg'>
            <source src={post.mediaURL} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        ) : (
          <img
            src={post.mediaURL}
            alt='post media'
            className='w-full mt-2 rounded-lg'
          />
        )
      )}

      </div>

      {/* Actions */}
      <div
        className='mt-4 flex justify-between items-center px-3 py-2 text-ascent-2
      text-base border-t border-[#66666645]'
      >
        {/* Nút Like */}
        <p className='flex gap-2 items-center text-base cursor-pointer' onClick={likePost}>
          {post?.likes?.some(like => 
            like.userId === user?.userId && like.postId === post.postId
          ) ? (
            <BiSolidLike size={20} color='blue' />
          ) : (
            <BiLike size={20} />
          )}
          {post?.likeCount} Likes
        </p>
          {/* Nút Comment */}
        <p
          className='flex gap-2 items-center text-base cursor-pointer'
          onClick={() => {
            handleShowComments();
          }}
        >
          <BiComment size={20} />
          {post?.commentCount} Comments
        </p>
        {/* Nút Share */}
        <p
            className='flex gap-2 items-center text-base cursor-pointer'
            // onClick={() => sharePost(post?._id)}
          >
            <BiShare size={20} />
            <span>Share</span>
          </p>
        {/* Nút Delete (chỉ hiển thị nếu user là chủ bài viết) */}
        {user?._id === post?.userId?._id && (
          <div
            className='flex gap-1 items-center text-base text-ascent-1 cursor-pointer'
            onClick={() => deletePost(post?._id)}
          >
            <MdOutlineDeleteOutline size={20} />
            <span>Delete</span>
          </div>
        )}
      </div>
      {/* COMMENTS */}
       {showComments === postId && (
        <div className='w-full mt-4 border-t border-[#66666645] pt-4 '>
          <CommentForm
            user={user}
            id={postId}
            getComments={getComments}
          />
          {/* danh sách comment*/ }
          {loading ? (
            <Loading />
          ) : comments?.length > 0 ? (
            comments.map((content) => (
              <div className='w-full py-2' key={content?.commentId}>
                <div className='flex gap-3 items-center mb-1'>
                  <Link to={"/profile/" + content?.user?.userId}>
                    <img
                      src={content?.user?.profileUrl ?? NoProfile}
                      alt={content?.user?.username}
                      className='w-10 h-10 rounded-full object-cover'
                    />
                  </Link>
                  <div>
                    <Link to={"/profile/" + content?.user?.userId}>
                      <p className='font-medium text-base text-ascent-1'>
                        {content?.user?.username} 
                      </p>
                    </Link>
                    <span className='text-ascent-2 text-sm'>
                      {moment(content?.timestamp ?? "2023-05-25").fromNow()}
                    </span>
                  </div>
                </div>

                <div className='ml-12'>
                  <p className='text-ascent-2'>{content?.comment}</p>

                  <div className='mt-2 flex gap-6'>
                    <p className='flex gap-2 items-center text-base text-ascent-2 cursor-pointer'>
                      {content?.likes?.includes(user?._id) ? (
                        <BiSolidLike size={20} color='blue' />
                      ) : (
                        <BiLike size={20} />
                      )}
                      {content?.likes?.length} Likes
                    </p>
                    <span
                      className='text-blue cursor-pointer'
                      onClick={() => setReplyComments(content?.commentId)}
                    >
                      Reply
                    </span>
                  </div>

                  {replyComments === content?._id && (
                    <CommentForm
                      user={user}
                      id={content?._id}
                      replyAt={content?.from}
                      getComments={() => getComments(post?._id)}
                    />
                  )}
                </div>

                {/* REPLIES

                <div className='py-2 px-8 mt-6'>
                  {comment?.replies?.length > 0 && (
                    <p
                      className='text-base text-ascent-1 cursor-pointer'
                      onClick={() =>
                        setShowReply(
                          showReply === comment?.replies?._id
                            ? 0
                            : comment?.replies?._id
                        )
                      }
                    >
                      Show Replies ({comment?.replies?.length})
                    </p>
                  )}

                  {showReply === comment?.replies?._id &&
                    comment?.replies?.map((reply) => (
                      <ReplyCard
                        reply={reply}
                        user={user}
                        key={reply?._id}
                        // handleLike={() =>
                        //   handleLike(
                        //     "/posts/like-comment/" +
                        //       comment?._id +
                        //       "/" +
                        //       reply?._id
                        //   )
                        // }
                      />
                    ))}
                </div> */}
              </div>
            ))
          ) : (
            <span className='flex text-sm py-4 text-ascent-2 text-center'>
              No Comments, be first to comment
            </span>
          )}
        </div>
      )}
    </div>
  );
};

export default PostCard;
